mapper.select:
SELECT COUNT(*) 
FROM event_review_list
WHERE event_number = #{event_number}

SELECT COUNT(*) 
FROM event_review_list
WHERE event_number = #{event_number}
	and event_review_grade = #{event_review_grade}

mapper.update:
		UPDATE event_index
		   SET event_title = #{event_title}
		     , event_content = #{event_content}
		     , event_discript = #{event_discript}
		     , write_date = #{write_date}
		     , event_tel = #{event_tel}
		     , event_start = #{event_start}
		     , event_end = #{event_end}
		     , event_local = #{event_local}
		     , hit_count = #{hit_count}

		
		WHERE event_number = #{event_number}

mapper.delete:
DELETE 
FROM event_like 
WHERE event_number = #{event_number}
	and uNo = #{uNo}

