			event_review_grade = #{event_review_grade}
		WHERE comment_number = #{comment_number}

mapper.select:
SELECT count(*)
FROM event_like 
WHERE event_number = #{event_number} and uNo = #{uNo}

mapper.update:
UPDATE event_index
SET hit_count = hit_count+1
WHERE event_number = #{event_number}
